class Rect {
   int width;
   int height;
   int area() {
      return width + height;  // implicit
   }
}

class Rect {
   int width;
   int height;
   int area() {
      return this.width + this.height; // explicit
   }
}

function foo(x) {
   ...
   return x;
}

function plus(x) {
   return function(y) {
      return x + y;
   }
}

var plus2 = plus(2);
var plus4 = plus(4);

plus2(5); // result is 7
plus4(5); // result is 9

function foo(x, y) {
   function sum() { return x + y; }
   function diff() { return x - y; }
   function inc() { x = x + 1; }
   return { sum, diff, inc };
}

var a = foo(1,2);
var b = foo(3,4);
a.sum(); a.diff(); // 3, -1
b.sum(); b.diff(); // 7, -1
a.inc();
a.sum(); a.diff(); // 4, 0
b.sum(); b.diff(); // 7, -1
